import bcrypt from "bcryptjs";
import fetch from "node-fetch";
import randomstring from "randomstring"

import dangnhapService from "../services/dangnhapService";
import mail from "../../helpers/nodemailer"

class dangnhapController {
  static async addTK(req, res) {      
    // =captcha==============
    if (!req.body.captcha) {
      return res.json({ success: false, msg: "Please select captcha" });
    }
    // Secret key
    const secretKey = "6LfWmNgUAAAAAByejHVatp1JvpJtRNccgNjzGeSx";
    const verifyURL =
      "https://www.google.com/recaptcha/api/siteverify?secret=" +
      secretKey +
      "&response=" +
      req.body.captcha +
      "&remoteip=" +
      req.connection.remoteAddress;
    // Make a request to verifyURL
    const body = await fetch(verifyURL).then(res => res.json());
    // If not successful
    if (body.success !== undefined && !body.success)
      return res.json({ success: false, msg: "Failed captcha verification" });
    // // If successful
    // // ============end captcha=================
    // to m b mt
    const token = randomstring.generate(4);
    const kichhoat = false
    const html = `Hi there,
    <br/>
    Cm n bn  ng k!
    <br/>
    Xin hy coppy m v dn vo  xc nhn  xc nhn email:
    <br/>
    M kch hot: <b>${token}</b>
    <br/>
    On the following page:
    <a href="http://localhost:3000/dangnhap/dangky/xacnhan/">http://localhost:3000/xacnhan</a>
    <br/><br/>
    Chc bn mt ngy tt lnh.` 

    let salt = bcrypt.genSaltSync(10);
    let hash = bcrypt.hashSync(req.body.matkhau, salt);
    let { email,tentaikhoan } = { ...req.body };
    let quyenhang = "Hc Vin";
    let trangthai = 1;
    if (email.trim().length > 0) {
      let kiemtra = await dangnhapService.getBylogin(email.toLowerCase());
      if (kiemtra.length > 0) {
        //  res.redirect("/?kq=0&mes=Email  tn ti");
        res.json({ success: false, msg: "Email  tn ti !!" });
      } else {
        let data = { ...req.body, matkhau: hash, quyenhang, trangthai ,token,kichhoat };
        try {        
          const createdTK = await dangnhapService.add(data);
          await mail(email,tentaikhoan,html);
          if (createdTK) {
            //  res.redirect(`/?kq=2&mes=ng k thnh cng. Xc nhn email  c ng nhp !`);
            res.json({ success: true, msg: "Xc nhn email  c th tip tc ng nhp !!" });
          } else {
            //  res.redirect(`/?kq=0&mes= c li xin ng k li !!!`);
            res.json({ success: false, msg: "Khng ng k thnh cng !!" });
          }
        } catch (error) {
          return error;
        }
      }
    }
  }

  //  xc nhn email 
  static async xacnhan(req, res) {
    const { token } = req.body;  
    try {
      const updatetk = await dangnhapService.xacNhanEmail(token);
      if (!updatetk) {
        res.redirect(`/?kq=0&mes=Li xc nhn !`);
      } else {
        res.redirect(`/?kq=1&mes=Xc nhn thnh cng, bn c th ng nhp !`);
      }
    } catch (error) {
      return error;
    }
  }
  static async getxacnhan(req, res) {    
    try {
    res.render("../views/outsite/xacnhanEmail.ejs")
    } catch (error) {
      return error;
    }
  }


  // Kt thc xc nhn 

  static async updatedtk(req, res) {
    const alteredtk = req.body;
    let { quyenhang } = { ...req.body };
    const { id } = req.params;
    if (!Number(id)) {
      console.log("ID Not a Number!!!");
    }
    try {
      const updatetk = await dangnhapService.UpdateTK(id, alteredtk);
      if (!updatetk) {
        res.redirect(`/admin/taikhoan/` + quyenhang + `?kq=0`);
      } else {
        res.redirect(`/admin/taikhoan/` + quyenhang + `?kq=1`);
      }
    } catch (error) {
      return error;
    }
  }

  static async getATk(req, res) {
    const { id } = req.params;

    if (!Number(id)) {
      console.log("id not a Number !!!");
    }

    try {
      const thetk = await dangnhapService.getByID(id);

      if (!thetk) {
        res.redirect("/admin/taikhoan");
      } else {
        res.redirect("/admin/taikhoan");
      }
    } catch (error) {
      return error;
    }
  }

  static async getlogin(req, res) {
    const { email, matkhau } = req.body;
    try {
      const thetk = await dangnhapService.getBylogin(email.toLowerCase());
      let user = thetk[0];
      if (thetk == 0) {
        res.redirect("/?kq=0&mes=Email khng hp l hoc cha c kch hot !!!");
      } else {
        switch (thetk[0].trangthai) {
          case 1:
            if (bcrypt.compareSync(matkhau, thetk[0].matkhau)) {
              req.session.user = user;
              switch (user.quyenhang) {
                case "Admin":
                  req.session.user = user;
                  res.redirect("/admin/index?kq=1&mes=ng nhp thnh cng.");                 
                  break;
                case "Nhn Vin":
                  req.session.user = user;
                  res.redirect("/admin/index?kq=1&mes=Nhn vin.");
                  break;
                default:
                  req.session.user = user;
                  res.redirect("/?kq=1&mes=ng nhp thnh cng.");
              }
            }else{
              res.redirect("/?kq=0&mes=Mt khu khng ng !");
            }
            break;
          default:
            res.redirect("/?kq=0&mes=Ti khon ca bn  b v hiu ha!!!");
        }
      }
      return error;
    } catch (error) {
      return error;
    }
  }
  // ================lay mat khau moi ==================
  static async laymatkhau(req, res) {   
    try { 
      const email = req.body.email;  
      const kiemtraemail = await dangnhapService.kiemtraemail(email);
      if (kiemtraemail) {
        const matkhaumoi = randomstring.generate(6);       
        const html = `Hi there,
        <br/>
        Cm n bn  s dng dch v ca chng ti!
        <br/>
        y l mt khu mi ca bn
        <br/>
        Mt khu mi: <b>${matkhaumoi}</b>
        <br/>
        Quay li trang ch  ng nhp no:
        <a href="http://localhost:3000/">http://localhost:3000/</a>
        <br/><br/>
        Chc bn mt ngy tt lnh.` 
         await mail(email,email,html);
        let salt = bcrypt.genSaltSync(10);
        let hash = bcrypt.hashSync(matkhaumoi, salt);
        let matkhau = hash ; 
        const updatetk1 = await dangnhapService.quenmatkhau(email, matkhau); 
        console.log(updatetk1);
        if (!updatetk1) {
          res.redirect(`/?kq=0&mes=Li !`);
        } else {
          res.redirect(`/?kq=1&mes=Xin hy check email  ly mt khu mi !`);
        }
      } else {
        res.redirect(`/?kq=0&mes=Email cha c ng k !`);
      }
