import diendanService from "../../services/dienDanService";
import baivietService from "../../services/baivietService";
class diendanController {
  static async getAll(req, res) {
    if (req.session.user) {
      try {
        let user = [];
        if (req.session.user) {
          user = req.session.user;
        }
        let { id } = req.params;
        const data1 = await diendanService.gettheotk(id);     
        const baiviet = await baivietService.getNew(); 
        let data = data1[0];
        if (data.length > 0) {
          res.render("../views/outsite/trangcanhan/listforum.ejs", {
            data,baiviet,
            user
          });
        } else {
          res.render("../views/outsite/trangcanhan/listforum.ejs", {
            data,baiviet,
            user,
            message: "Khong co hoc vien nao"
          });
        }
        return 0;
      } catch (error) {
        return error;
      }
    } else {
      return res.redirect("/");
    }
  }

  static async Delete(req, res) {
    if (req.session.user) {
      let { id } = req.params;
      if (!Number(id)) {
        console.log("ID not Number !!!");
        res.redirect("/taikhoan/canhan?kq=0&mes=C li!!!");
      }
      try {
        let Xoa = await diendanService.delete(id);
        if (Xoa) {
          res.redirect("/taikhoan/canhan?kq=1&mes=Thnh Cng!!");
        } else {
          res.redirect("/taikhoan/canhan?kq=0&mes=C li!!!");
        }
        return;
      } catch (error) {
        return error;
      }
    } else {
      return res.redirect("/");
    }
  }

  static async update(req, res) {
    if (req.session.user) {
    const altered = req.body;
    const { id } = req.params;
    try {
         
      const update = await diendanService.Update(id, altered);
      
      if (!update) {
        res.redirect(`/taikhoan/diendan/`+ id +`?kq=0&mes=Khng thnh cng !!!`);
      } else {
        res.redirect(`/taikhoan/diendan/`+ id +`?kq=1&mes=Cp nht thnh cng.`);
      }
      return;
    } catch (error) {
      return error;
    }
  } else {
    req.redirect("/")
  }
  }

  static async getA(req, res) {
    if (req.session.user) {
      try {
        let user = [];
        if (req.session.user) {
          user = req.session.user;
        }
        let { id } = req.params;
        const data1 = await diendanService.getByID(id);     
        const baiviet = await baivietService.getNew();
        let data = data1[0][0];              
        if (data.length > 0) {
          res.render("../views/outsite/trangcanhan/updatebaiviet.ejs", {
            data,baiviet,
            user
          });
        } else {
          res.render("../views/outsite/trangcanhan/updatebaiviet.ejs", {
            data,baiviet,
            user
          });
        }
        return 0;
      } catch (error) {
        return error;
      }
    } else {
      return res.redirect("/");
    }
  }
}

export default diendanController;

