import date from "s-date";
import lopHocService from "../services/lopHocService";
import khoaHocService from "../services/khoaHocService";
import giangVienService from "../services/giangVienService";
import dangkyService from "../services/dangkyService";
import phongHocService from "../services/phongHocService";
// trang thai 0 hon thnh , 1 sp khai ging , 2 ang hc , 3 ang ch
class lophocController {
  static async getAll(req, res) {
    if (req.session.user.quyenhang == "Admin" || req.session.user.quyenhang == "Nhn Vin") {
      try {
        const user = req.session.user
        let trangthai = 1;
        const data1 = await lopHocService.getAll(trangthai);
        const khoahoc = await khoaHocService.getKH();
        const phonghoc = await phongHocService.getsudung();
        const giangvien = await giangVienService.getcolam();
        const data = data1[0];
        if (data.length > 0) {
          res.render("../views/admin/lophoc/listlophoc.ejs", {
            data,user,
            trangthai,
            date,
            phonghoc,
            khoahoc,
            giangvien
          });
        } else {
          res.render("../views/admin/lophoc/listlophoc.ejs", {
            data,user,
            date,
            trangthai,
            khoahoc,
            giangvien,
            phonghoc,
            message: "Khong co hoc vien nao"
          });
        }
        return 0;
      } catch (error) {
        return error;
      }
    } else {
      return res.redirect("/");
    }
  }
  static async get1lop(req, res) {
    if (req.session.user.quyenhang == "Admin" || req.session.user.quyenhang == "Nhn Vin") {
      try {
        const user = req.session.user
        let trangthai = req.params.trangthai;
        const data1 = await lopHocService.get1lop(trangthai);
        const khoahoc = await khoaHocService.getAll();
        const phonghoc = await phongHocService.getsudung();
        const giangvien = await giangVienService.getcolam();
        const data = data1[0];
        if (data.length > 0) {         
          res.render("../views/admin/lophoc/listlophoc.ejs", {
            data, user,
            phonghoc,
            trangthai,
            date,
            khoahoc,
            giangvien
          });
        } else {
          res.render("../views/admin/lophoc/listlophoc.ejs", {
            data, user,
            date,
            phonghoc,
            trangthai,
            khoahoc,
            giangvien,
            message: "Khong co hoc vien nao"
          });
        }
        return 0;
      } catch (error) {
        return error;
      }
    } else {
      return res.redirect("/");
    }
  }

  static async Delete(req, res) {
    if (req.session.user.quyenhang == "Admin" || req.session.user.quyenhang == "Nhn Vin") {
      let { id } = req.params;
      if (!Number(id)) {
        console.log("ID not Number !!!");
        res.redirect("/admin/lophoc");
      }
      try {
        let Xoa = await lopHocService.delete(id);
        if (Xoa) {
          res.status(204).redirect("/admin/lophoc");
        } else {
          res.redirect("/admin/lophoc");
        }
        return;
      } catch (error) {
        return error;
      }
    } else {
      return res.redirect("/");
    }
  }

  static async add(req, res) {
    let trangthai = 3;
    let data = { ...req.body, trangthai };

    try {
      const created = await lopHocService.add(data);     
      switch(created) {
        case 0:
          res.redirect("/admin/lophoc/dem/3?kq=0&mes=B trng lp !")
          break;
        case 1:
          res.redirect("/admin/lophoc/dem/3?kq=0&mes=Khng thm c !")
          break;
        default:
          res.redirect("/admin/lophoc/dem/3?kq=1&mes=Thm thnh cng !")
      }
      
    } catch (error) {
      return error;
    }
  }

  static async update(req, res) {
    const altered = req.body;
    const { id } = req.params;   
    try {      
      const update = await lopHocService.Update(id, altered);     
      if (!update) {
        res.redirect("/admin/lophoc?kq=0&mes=Cp nht khng thnh cng !!!");
      } else {
       
        res.redirect("/admin/lophoc?kq=1&mes=Thnh cng !");
      }
      return;
    } catch (error) {
      return error;
    }
  }

  static async getAllhocvien(req, res) {
    if (req.session.user.quyenhang == "Admin" || req.session.user.quyenhang == "Nhn Vin") {
      try {
        const user = req.session.user
        const { id } = req.params;
        const data = await dangkyService.getByIDLop(id);
        //  console.log(data);

        const lophoc = await lopHocService.getByID(id);
        if (data.length > 0) {
          res.render("../views/admin/dangky/listdangky.ejs", {
            data, user,
            date,
            lophoc
          });
        } else {
          res.render("../views/admin/dangky/listdangky.ejs", {
            data, user,
            lophoc,
            date,
            message: "Khong co hoc vien nao"
          });
        }
        return 0;
      } catch (error) {
        return error;
      }
    } else {
      return res.redirect("/");
    }
  }
}

export default lophocController;

