import { validationResult } from "express-validator";

import diendanService from "../services/dienDanService";

class diendanController {
  static async getAll(req, res) {
    if (req.session.user.quyenhang == "Admin" || req.session.user.quyenhang == "Nhn Vin") {
      try {
        const user = req.session.user
        const data1 = await diendanService.getAllAd();
        let data = data1[0];
        if (data.length > 0) {
          res.render("../views/admin/forum/listforum.ejs", { data , user});
        } else {
          res.render("../views/admin/forum/listforum.ejs", {
            data, user,
            message: "Khong co hoc vien nao"
          });
        }
        return 0;
      } catch (error) {
        return error;
      }
    } else {
      return res.redirect("/");
    }
  }

  static async Delete(req, res) {
    if (req.session.user.quyenhang == "Admin" || req.session.user.quyenhang == "Nhn Vin") {
      let { id } = req.params;
      if (!Number(id)) {        
        res.redirect("/admin/diendan?kq=0&mes=Li d liu !!!");
      }
      try {
        let Xoa = await diendanService.delete(id);
        if (Xoa) {
          res.redirect("/admin/diendan?kq=1&mes=Thnh Cng!!");
        } else {
          res.redirect("/admin/diendan?kq=0&mes=Tht Bi!!!");
        }
        return;
      } catch (error) {
        return error;
      }
    } else {
      return res.redirect("/");
    }
  }

}

export default diendanController;

